import React, { useState, useEffect } from 'react';
import './navBar.css';
import logo_light from '../Navbar_React_Assets/assets/logo-black.png';
import logo_dark from '../Navbar_React_Assets/assets/logo-light.png';
import toggle_dark from '../Navbar_React_Assets/assets/day.png';
import toggle_light from '../Navbar_React_Assets/assets/night.png';

export default function Navbar() {
  const [theme, setTheme] = useState('light');

  useEffect(() => {
    document.body.className = theme;
  }, [theme]);

  const toggleTheme = () => {
    setTheme(theme === 'light' ? 'dark' : 'light');
  };

  return (
    <div className='navbar'>
      <img src={theme==='light'?logo_light:logo_dark} alt="Logo" className='logo' />
      <div className='links'>
        <a  href='/'>Home</a>
        <a  href='/About'>About</a>
        <a  href='/Contact'>Contact</a>
      </div>
      <img
        src={theme === 'light' ? toggle_light : toggle_dark}
        alt="Toggle Theme"
        className='toggle-icon'
        onClick={toggleTheme}
      />
    </div>
  );
}
