import React, { useState, useEffect } from 'react';
import { ChevronLeft, ChevronRight } from 'lucide-react';
import slide1 from '../img/slide01.png'
import slide2 from '../img/slide02.png'
import slide3 from '../img/slide03.png'
import slide4 from '../img/slide04.png'
import slide5 from '../img/slide05.png'

const images = [
  {
    src:{slide1},
    caption: 'Library'
  },
  {
    src:{sli},
    caption: 'Study'
  },
  {
    src:'../img/slide03.png',
    caption: 'Learning'
  },
  {
    src:'../img/slide04.png',
    caption: 'Education'
  },
  {
    src:'../img/slide05.png',
    caption: 'Classroom'
  }
];

function App() {
  const [currentIndex, setCurrentIndex] = useState(0);
  const [isAnimating, setIsAnimating] = useState(false);
  const [dateTime, setDateTime] = useState(new Date());
  const [welcomeMessage, setWelcomeMessage] = useState("Welcome!");
  const [userName, setUserName] = useState("");

  useEffect(() => {
    const slideTimer = setInterval(() => {
      nextSlide();
    }, 5000);

    const clockTimer = setInterval(() => {
      setDateTime(new Date());
    }, 1000);

    return () => {
      clearInterval(slideTimer);
      clearInterval(clockTimer);
    };
  }, [currentIndex]);

  const getGreetingMessage = () => {
    const hours = dateTime.getHours();
    if (hours < 12) {
      return "Good Morning...!";
    } else if (hours < 18) {
      return "Good Afternoon...!";
    } else {
      return "Good Evening...!";
    }
  };

  const handleInputChange = (event) => {
    const name = event.target.value;
    setUserName(name);
    setWelcomeMessage(name ? `Welcome, ${name}!` : "Welcome!");
  };

  const nextSlide = () => {
    if (isAnimating) return;
    setIsAnimating(true);
    setCurrentIndex((prevIndex) => (prevIndex + 1) % images.length);
    setTimeout(() => setIsAnimating(false), 500);
  };

  const prevSlide = () => {
    if (isAnimating) return;
    setIsAnimating(true);
    setCurrentIndex((prevIndex) => (prevIndex - 1 + images.length) % images.length);
    setTimeout(() => setIsAnimating(false), 500);
  };

  return (
    <div className="min-h-screen bg-gray-100">
      {/* Slideshow Section */}
      <div className="relative h-[60vh] overflow-hidden">
        <div className="relative w-full h-full">
          {images.map((image, index) => (
            <div
              key={index}
              className={`absolute w-full h-full transition-all duration-500 ease-in-out ${
                index === currentIndex
                  ? 'opacity-100 translate-x-0'
                  : index < currentIndex
                  ? 'opacity-0 -translate-x-full'
                  : 'opacity-0 translate-x-full'
              }`}
            >
              <img
                src={image.url}
                alt={image.caption}
                className="w-full h-full object-cover"
              />
              <div className="absolute inset-0 bg-black/30" />
            </div>
          ))}
        </div>

        {/* Navigation Buttons */}
        <button
          onClick={prevSlide}
          className="absolute left-4 top-1/2 -translate-y-1/2 p-2 rounded-full bg-white/20 text-white hover:bg-white/30 transition-colors"
          aria-label="Previous slide"
        >
          <ChevronLeft className="w-6 h-6" />
        </button>
        <button
          onClick={nextSlide}
          className="absolute right-4 top-1/2 -translate-y-1/2 p-2 rounded-full bg-white/20 text-white hover:bg-white/30 transition-colors"
          aria-label="Next slide"
        >
          <ChevronRight className="w-6 h-6" />
        </button>

        {/* Welcome Content Overlay */}
        <div className="absolute inset-0 flex flex-col items-center justify-center text-white">
          <h2 className="text-4xl font-bold mb-4 animate-fade-in">
            {getGreetingMessage()}
          </h2>
          <div className="text-center space-y-4">
            <h3 className="text-2xl font-semibold">{welcomeMessage}</h3>
            <input
              type="text"
              value={userName}
              onChange={handleInputChange}
              placeholder="Enter your name here"
              className="px-4 py-2 rounded-full bg-white/20 backdrop-blur-sm text-white placeholder-white/70 border border-white/30 focus:outline-none focus:ring-2 focus:ring-white/50 w-64 text-center"
            />
          </div>
        </div>
      </div>

      {/* Content Section */}
      <div className="max-w-4xl mx-auto px-4 py-12">
        <div className="text-center space-y-6">
          <h1 className="text-4xl font-bold text-gray-900">Welcome to EduLearn!</h1>
          <p className="text-lg text-gray-700 leading-relaxed">
            Your one-stop destination for quality educational resources. Dive into a world 
            of knowledge with our comprehensive collection of school papers, engaging 
            educational videos, and concise short notes. Empower your learning journey 
            with EduLearn, where education meets excellence!
          </p>
        </div>
      </div>
    </div>
  );
}

export default App;
